App::uses('ModelBehavior', 'Model');
class StatBehavior extends ModelBehavior {
	public function percent(Model $model, $complete, $total) {
		$return = false;
		if ($total > 0 ) {
			if ($complete <= $total) {
				$return = round(($complete / $total) * 100, 2);
			} else {
				$return = 100;
			}
		}
		return $return;
	}
}