App::uses('AppModel', 'Model');

class City extends AppModel {
/**
 * Grabs a list of cities for a given province
 * @param  string $province
 * @return array
 */
	public function getCities($province) {
		$results = $this->find('list', array(
			'fields' => array(
				'city'
			),
			'conditions' => array(
				'province' => $province
			)
		));
		return $results;
	}
}