/**
 * Before we save, if the _en field isn't set (on a new record)
 * set it to the same as _fr
 * 
 */
	public function beforeSave($options = array()) {
		parent::beforeSave($options);
		if (!empty($this->data['Restaurant']['name'])) {
			$this->data['Restaurant']['url'] = $this->generateUrlFromName($this->data['Restaurant']['name']);
		}
	}

/**
 * Gets a restaurant's name from it's record
 * @param  string $id  UUID of the restaurant
 * @return mixed     Name of the restaurant on success, false on failure
 */
	public function getRestaurantName($id) {
		$result = $this->find('first', array(
			'conditions' => array(
				'Restaurant.id' => $id
			),
			'fields' => array(
				'Restaurant.name'
			)
		));
		if ($result) {
			return $result['Restaurant']['name'];
		}
		return false;
	}
